.segment	"CODE"

.proc	_update_health: near

.segment	"CODE"

;
; for (i = 0; i < p1_max_health; ++i)
;
	lda     #$00
	sta     _i
L0009:	lda     _i
	cmp     _p1_max_health
	bcs     L0003
;
; one_vram_buffer((i > p1_health) ? TILE_HEART_EMPTY : TILE_HEART_FULL, NTADR_A(i + 2, 2));
;
	cmp     _p1_health
	bcc     L000A
	beq     L000A
	lda     #$5C
	jmp     L000B
L000A:	lda     #$5B
L000B:	jsr     pusha
	ldx     #$00
	lda     _i
	clc
	adc     #$02
	bcc     L0008
	inx
L0008:	ora     #$40
	pha
	txa
	ora     #$20
	tax
	pla
	jsr     _one_vram_buffer
;
; for (i = 0; i < p1_max_health; ++i)
;
	inc     _i
	jmp     L0009
;
; }
;
L0003:	rts

.endproc

